import React, { Component } from 'react';
import proptypes from 'prop-types';
import Config from '../config/config';

const FormContainer = styled.div`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 100%;
  text-align: center;
`;

const StoreSelect = styled.select.attrs({
  id: 'store-select',
})`
  -webkit-appearance: none;
  margin: 25px auto 50px;
  font-size: 1.2em;
  width: 50%;
  color: #777;
  font-weight: 300;
  padding: 5px;
  background-color: #fff;
  //box-shadow: 1px 2px 10px -2px rgba(0,0,0,0.3);
  -webkit-border-radius: 3px;
  border-radius: 3px;
  transition: all 375ms ease-in-out;
`;

const StoreSelectLabel = styled.label.attrs({
  htmlFor: 'store-select',
})`
  font-size: 1.5em;
`;

const SubmitButton = styled.input.attrs({
  type: 'button',
  value: 'Submit',
})`
  -webkit-appearance: none;
  width: 30%;
  background-color: #65236E;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 8px;
  font-size: 1.2em;
`;


class WelcomeScreen extends Component {
  constructor(props) {
    super(props);
    this.StoreSelect = React.createRef();
  }

  goToSearch = () => {
    Config.storeNumber = this.StoreSelect.current.value;
    this.props.history.push(`/search/${Config.storeNumber}`);
  };

  render() {
    return (
      <FormContainer>
        <StoreSelectLabel>Select your Store!</StoreSelectLabel>
        <div>
          <StoreSelect innerRef={this.StoreSelect}>
            <option value={20}>20</option>
            <option value={607}>607</option>
          </StoreSelect>
        </div>
        <SubmitButton onClick={this.goToSearch} />
      </FormContainer>
    );
  }
}

export default WelcomeScreen;

WelcomeScreen.propTypes = {
  history: proptypes.shape({
    push: proptypes.func,
  }).isRequired,
};
