import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
  state = {
      number : 10
    }
  render() {
    var images=[],
    x=-1;
    images[0]="./assets/Attract-loop-image.png";
    images[1]="./assets/Barcode.png";
    images[2]="./assets/Attract-loop-image.png";
    images[3]="./assets/Attract-loop-image.png";
    function displayNextImage(){
      x=(x===images.length-1)?0:x+1;document.getElementById("img").src=images[x];
    }
    function displayPreviousImage(){
      x=(x<=0)?images.length-1:x-1;
      document.getElementById("img").src=images[x];
    }
    function startTimer(){
      setInterval(displayNextImage,3000);
    }
    return (
      <div className="App" onLoad={startTimer}>
        <header className="App-Header">
          <h1> Savings & Coupons</h1>
        </header>
        <div className="couponScreenBackground">  </div>
        <div id="one" className="screen">
            <div class="container">
                <span>Tap anywhere to start</span>
            </div>
        </div>
      </div>
    );
  }
}

export default App;
