import React, { Component } from 'react';
import './AttractLoop.css';
import '../assets/Barcode.png';
import { Slide } from 'react-slideshow-image';


class AttractLoop extends Component {
  state = {
      number : 10
    }
  render() {
    const Barcode_Image = require('../assets/Barcode.png');

    const slideImages = [
      Barcode_Image,
      'images/slide_3.jpg',
      'images/slide_4.jpg'
    ];

    const properties = {
      duration: 5000,
      transitionDuration: 500,
      infinite: true,
      indicators: true,
      arrows: true
    }

    return (
      <div className="App" onClick={() => alert("This should check for user authentication")}>
        <header className="App-Header">
          <h1> Savings & Coupons</h1>
        </header>
        <div className="couponScreenBackground">  </div>
        <Slide {...properties}>
                <div className="each-slide">
                  <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
                    <span>Slide 1</span>
                  </div>
                </div>
                <div className="each-slide">
                  <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
                    <span>Slide 2</span>
                  </div>
                </div>
                <div className="each-slide">
                  <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
                    <span>Slide 3</span>
                  </div>
                </div>
        </Slide>

        <div id="one" className="screen">
            <div className="container">
                <span>Tap anywhere to start</span>
            </div>
        </div>
        <div className="barcodeImage">
          <h3> Scan card to start </h3>
          <img src={Barcode_Image} height="100px" alt="Barcode"/>
        </div>
      </div>
    );
  }
}

export default AttractLoop;
