import React, { Component } from 'react';
import proptypes from 'prop-types';
import Config from '../config/config';
import styled from 'styled-components';

const ImageContainer = styled.div`
  position: relative;


  width: 100%;
  text-align: center;
`;

const ScanDiv = styled.div`
    position: absolute; 
    top: 10%;
    transform: translateY(-50%);
    width: 100%;
    text-align: center;
`;
const TapButton = styled.button`
    display:block;
    width:50px;
    height:50px;
    line-height:50px;
    border: 2px solid #f5f5f5;
    border-radius: 50%;
    color:#f5f5f5;
    text-align:center;
    text-decoration:none;
    background: #464646;
    box-shadow: 0 0 3px gray;
    font-size:20px;
    font-weight:bold;
    left:0;



`;
const ScanButton= styled.button`
    display:block;
    width:50px;
    height:50px;
    line-height:50px;
    border: 2px solid #f5f5f5;
    border-radius: 50%;
    color:#f5f5f5;
    text-align:center;
    text-decoration:none;
    background: #464646;
    box-shadow: 0 0 3px gray;
    font-size:20px;
    font-weight:bold;
    right:0;

`;
class LoadingPage extends Component{
    constructor(props){
        super(props);
        this.state = {
            numberOfSelectedScannedVerification: 0,
            numberOfSelectedMobileVerification: 0,
        }


    }
    updateScannedVerification = () => {
        const prev = this.state.numberOfSelectedScannedVerification;
        this.setState({numberOfSelectedScannedVerification: prev+1});
    };
    updateMobileVerification = () => {
        const prev = this.state.numberOfSelectedMobileVerification;
        this.setState({numberOfSelectedMobileVerification: prev+1});
    };

    render(){
        return(

            <ScanDiv>
                Scan & Coupons

            <ImageContainer>
                "Inside the loading page "

                {alert("Inside loading page")}    
                <TapButton>"Tap to enter Phone number"</TapButton>
                <ScanButton>"Tap to Scan"</ScanButton>
            </ImageContainer>
            </ScanDiv>

        );


    }
} 


export default LoadingPage;
