import React, { Component } from 'react';
import proptypes from 'prop-types';
import Config from '../config/config';
import styled from 'styled-components';

const ImageContainer = styled.div`
  position: relative;
  top: 50%;
  transform: translateY(-50%);
  width: 100%;
  text-align: center;
`;

const ScanDiv = styled.div`
    position: absolute; 
    top: 50%;
    transform: translateY(-10%);
    width: 100%;
    text-align: center;
`;

class LoadingPage extends Component{
    constructor(props){
        super(props);
        this.state = {
            numberOfSelectedScannedVerification: 0,
            numberOfSelectedMobileVerification: 0,
        }


    }
    updateScannedVerification = () => {
        const prev = this.state.numberOfSelectedScannedVerification;
        this.setState({numberOfSelectedScannedVerification: prev+1});
    };
    updateMobileVerification = () => {
        const prev = this.state.numberOfSelectedMobileVerification;
        this.setState({numberOfSelectedMobileVerification: prev+1});
    };

    render(){
        return(
            <ScanDiv>
                Scan & Coupons
            <ImageContainer>
                "Inside the loading page "
                {alert("Inside loading page")}    
            </ImageContainer>
            </ScanDiv>
        );


    }
} 


export default LoadingPage;
